<template>
  <nav class="navbar" role="navigation" aria-label="main navigation" :style=" netflix_black ? 'background-color: #222222' : 'background-color: black;'">
    <div class="container">
      <div class="loading">
        <loading :active.sync="loading" :can-cancel="false" :is-full-page="fullpage"></loading>
      </div>
      <div class="navbar-brand">
        <div class="navbar-item nav-link">
          <h3 class="title has-text-netflix is-3" @click="homeroute">
            {{ siteName }}
          </h3>
        </div>
        <a
          role="button"
          style="color: #e50914;"
          :class="'navbar-burger burger ' + (isActive ? 'navbar-active' : '')"
          aria-label="menu"
          aria-expanded="false"
          data-target="navbarBasicExample"
          @click="burgerClick"
        >
          <span aria-hidden="true"></span>
          <span aria-hidden="true"></span>
          <span aria-hidden="true"></span>
        </a>
      </div>
      <div
        id="navbarBasicExample"
        :class="'navbar-menu ' + (isActive ? 'is-active' : '')"
        style="background-color: inherit"
      >
        <div class="navbar-start">
          <a
            class="navbar-item"
            title="OTP Registration"
            v-if="!logged"
            @click="gotoPage('/otp/', 'register')"
           >
          <span>Register</span>
          </a>
          <a
            class="navbar-item"
            title="Request Access"
            v-if="!logged"
            @click="gotoPage('/request/user/', 'register')"
           >
          <span>Request Access</span>
          </a>
          <a
            class="navbar-item"
            title="Login"
            v-if="!logged"
            @click="gotoPage('/', 'login')"
           >
          <span>Login</span>
          </a>
          <a
            class="navbar-item"
            v-show="logged"
            v-for="(link, index) in quicklinks.slice(0,3)"
            :title="link.displayname"
            v-bind:key="index"
            @click="gotoPage('/'+ link.link + '/')"
           >
          <span>{{ link.displayname }}</span>
          </a>
          <a
            class="navbar-item"
            v-show="logged"
            title="All"
            @click="gotoPage('/')"
           >
          <span>View All</span>
          </a>
          <div
            :class="ismobile ? gddropdown ? 'navbar-item has-dropdown is-active' : 'navbar-item has-dropdown' : 'navbar-item has-dropdown is-hoverable' "
           @click="ismobile ? gddropdown = !gddropdown : '' " v-if="!logged">
            <a class="navbar-link" style="background-color: inherit;">Space</a>
            <div class="navbar-dropdown is-boxed">
              <a
                class="navbar-item"
                @click="changeItem(item)"
                v-for="(item, index) in gds"
                v-bind:key="index"
                >{{ item.name }}</a
              >
            </div>
          </div>
        </div>
        <div class="navbar-end">
          <!-- is-hidden-desktop -->
          <div
            :class="ismobile ? gddropdown ? 'navbar-item has-dropdown is-active' : 'navbar-item has-dropdown' : 'navbar-item has-dropdown is-hoverable' "
           @click="ismobile ? gddropdown = !gddropdown : '' " v-if="logged">
            <a class="navbar-link" style="background-color: inherit;">Space</a>
            <div class="navbar-dropdown is-boxed">
              <a
                class="navbar-item"
                @click="changeItem(item)"
                v-for="(item, index) in gds"
                v-bind:key="index"
                >{{ item.name }}</a
              >
            </div>
          </div>
          <div v-if="logged" class="navbar-item" v-show="showSearch">
            <div class="field is-grouped">
              <p class="control has-icons-right is-dark" style="width:100%;">
                <input
                  class="input is-rounded search-input"
                  @keyup.enter="query"
                  v-model="param"
                  type="search"
                  :placeholder="$t('search.placeholder')"
                />
                <span class="icon is-small is-right" style="padding:0 5px;">
                  <i class="fas fa-search"></i>
                </span>
              </p>
            </div>
          </div>
          <a
            class="navbar-item"
            title="Profile"
            @click="gotoPage('/' ,'settings')"
            v-if="logged"
           >
           <span class="icon">
            <i class="far fa-user"></i>
          </span>
          <span class="">{{ user.name.slice(0,10) }}</span>
          </a>
          <a
            class="navbar-item"
            title="Admin Panel"
            v-if="logged && admin"
            @click="gotoPage('/','admin')"
           >
           <span class="icon">
            <i class="fas fa-user-shield"></i>
          </span>
          <span  class="is-hidden-desktop">Admin Zone</span>
          </a>
          <a
            class="navbar-item"
            title="Logout"
            @click="logout"
            v-if="logged"
           >
           <span class="icon">
            <i class="fas fa-sign-out-alt"></i>
          </span>
          <span class="is-hidden-desktop">Logout</span>
          </a>
          <a
            :class="ismobile ? 'navbar-item' : 'navbar-item is-hidden'"
            @click.stop="$refs.viewMode.toggleMode"
          >
            <viewMode ref="viewMode" />
          </a>
        </div>
      </div>
    </div>
  </nav>
</template>

<script>
import ViewMode from "@/layout/viewmode";
import Loading from 'vue-loading-overlay';
export default {
  components: {
    ViewMode,
    Loading,
  },
  created() {
    this.$bus.$on('logged', () => {
      this.loginorout();
      this.changeNavbarStyle()
    })
    this.$bus.$on('logout', () => {
      this.loginorout();
      this.changeNavbarStyle()
    })
    this.$bus.$on('td', () => {
      this.quicklinks = window.quickLinks.filter((links) => {
        return links.root == this.gdindex
      })[0].link;
    })
    this.loginorout();
    this.active = false;
    this.siteName = document.getElementsByTagName("title")[0].innerText;
    if (window.gds && window.gds.length > 0) {
      this.gds = window.gds.map((item, index) => {
        return {
          name: item,
          id: '/'+index+':',
          index: index,
        };
      });
      this.chooseGD();
    }
    if (window.MODEL) {
      this.param = window.MODEL.q ? window.MODEL.q : "";
    }
  },
  data: function() {
    return {
      user: {},
      siteName: "",
      active: false,
      param: "",
      currgd: {},
      loading: false,
      navbarStyle: "",
      netflix_black: false,
      mouseover: false,
      backgroundClass: "",
      fullpage: true,
      logged: false,
      admin: false,
      quicklinks: [],
      superadmin: false,
      gds: [],
      gdindex: '',
      gddropdown: false,
      isActive: false,
      eyes:
        "data:image/png;base64,",
    };
  },
  methods: {
    chooseGD() {
      let index = this.$route.params.id;
      if (this.gds && this.gds.length >= index) {
        this.currgd = this.gds[index];
        this.gdindex = this.gds[index].index;
      }
    },
    changeItem(item) {
      this.$bus.$emit("td", "TD Changed");
      this.currgd = item;
      this.$router.push({
        path: '/'+item.index+':home/',
      });
    },
    query() {
      if (this.param) {
        this.isActive = !this.isActive;
        this.$router.push({
          path: this.currgd.id.match("/[0-9]+:") + "search?q=" + this.param,
        });
      }
    },
    burgerClick() {
      this.isActive = !this.isActive;
    },
    hoverclick() {
      this.active = !this.active
    },
    loginorout() {
      var token = localStorage.getItem("tokendata");
      var user = localStorage.getItem("userdata");
      var hyBridToken = localStorage.getItem("hybridToken");
      console.log(hyBridToken);
      if(hyBridToken && hyBridToken != null || hyBridToken != undefined){
        const hybridData = JSON.parse(this.$hash.AES.decrypt(hyBridToken, this.$pass).toString(this.$hash.enc.Utf8))
        console.log(hybridData);
        if(hybridData.user){
          this.user = hybridData;
          this.logged = true;
          this.admin = false;
          this.superadmin = false;
          this.loading = false;
        } else {
          localStorage.removeItem("hybridToken");
          this.gotoPage("/", "login")
        }
      } else if (user != null && token != null){
        var userData = JSON.parse(this.$hash.AES.decrypt(user, this.$pass).toString(this.$hash.enc.Utf8));
        if(userData.admin && !userData.superadmin){
          this.user = userData;
          this.logged = true;
          this.admin = true;
        } else if(userData.admin && userData.superadmin){
          this.user = userData;
          this.logged = true;
          this.admin = true;
          this.superadmin = true
        } else {
          this.user = userData;
          this.logged = true
        }
      } else {
        this.logged = false
      }
    },
    homeroute() {
      this.$router.push({ path: '/'+ this.gdindex + ':' + 'home/' })
    },
    gotoPage(url, cmd) {
      this.isActive = !this.isActive;
      this.loading = true;
      if(cmd){
        this.$router.push({ path: '/'+ this.gdindex + ':' + cmd + url })
      } else {
        this.$router.push({ path: '/'+ this.gdindex + ':' + url })
      }
      setTimeout(() => {
        this.loading = false;
      }, 500)
    },
    logout() {
      this.loading = true;
      this.isActive = !this.isActive;
      var token = localStorage.getItem("tokendata")
      var user = localStorage.getItem("userdata");
      var hyBridToken = localStorage.getItem("hybridToken");
      if(hyBridToken && hyBridToken != null || hyBridToken != undefined){
        localStorage.removeItem("hybridToken");
        this.$bus.$emit("logout", "User Logged Out");
        this.loading = false;
        this.$router.push({ name: 'results' , params: { id: this.gdindex, cmd: "result", success:true, data: "You are Being Logged Out. Please Wait", redirectUrl: '/', tocmd:'home' } })
      } else if (user != null && token != null){
        localStorage.removeItem("tokendata");
        localStorage.removeItem("userdata");
        this.$bus.$emit("logout", "User Logged Out");
        this.loading = false;
        this.$router.push({ name: 'results' , params: { id: this.gdindex, cmd: "result", success:true, data: "You are Being Logged Out. Please Wait", redirectUrl: '/', tocmd:'home' } })
      } else {
        this.loading = false;
      }
    },
    changeNavbarStyle() {
      if(this.$route.name == 'home' && !this.logged){
        this.navbarStyle = "transparent"
        this.backgroundClass = "home-back";
      } else {
        this.navbarStyle = "black";
        this.backgroundClass = "none";
      }
    }
  },
  computed: {
    showSearch() {
// Folder does not support searching
      return window.MODEL ? window.MODEL.root_type < 2 : true
    },
    ismobile() {
      var width = window.innerWidth > 0 ? window.innerWidth : screen.width;
      if(width > 966){
        return false
      } else {
        return true
      }
    }
  },
  mounted() {
    this.netflix_black = window.themeOptions.prefer_netflix_black
    this.quicklinks = window.quickLinks.filter((links) => {
      return links.root == this.gdindex
    })[0].link;
    this.changeNavbarStyle();
  },
  watch: {
    "$route.params.id": "chooseGD",
    "$route": function() {
      if(this.$route.name == 'home' && !this.logged){
        this.navbarStyle = "transparent";
        this.backgroundClass = "home-back";
      } else {
        this.navbarStyle = "black";
        this.backgroundClass = "none";
      }
    }
  },
};
</script>
